import Vue from "vue";
import Router from "vue-router";
Vue.use(Router);
/* Layout */

import Layout from "@/layout";
/* Router Modules */

import tableRouter from "./modules/table";
/**
 * Note: sub-menu only appear when route children.length >= 1
 * Detail see: https://panjiachen.github.io/vue-element-admin-site/guide/essentials/router-and-nav.html
 *
 * hidden: true                   if set true, item will not show in the sidebar(default is false)
 * alwaysShow: true               if set true, will always show the root menu
 *                                if not set alwaysShow, when item has more than one children route,
 *                                it will becomes nested mode, otherwise not show the root menu
 * redirect: noRedirect           if set noRedirect will no redirect in the breadcrumb
 * name:'router-name'             the name is used by <keep-alive> (must set!!!)
 * meta : {
    roles: ['admin','editor']    control the page roles (you can set multiple roles)
    title: 'title'               the name show in sidebar and breadcrumb (recommend set)
    icon: 'svg-name'             the icon show in the sidebar
    noCache: true                if set true, the page will no be cached(default is false)
    affix: true                  if set true, the tag will affix in the tags-view
    breadcrumb: false            if set false, the item will hidden in breadcrumb(default is true)
    activeMenu: '/example/list'  if set path, the sidebar will highlight the path you set
  }
 */

/**
 * constantRoutes
 * a base page that does not have permission requirements
 * all roles can be accessed
 */

export var constantRoutes = [{
  path: "/redirect",
  component: Layout,
  hidden: true,
  children: [{
    path: "/redirect/:path*",
    component: function component() {
      return import("@/views/redirect/index");
    }
  }]
}, {
  path: "/login",
  component: function component() {
    return import("@/views/login/index");
  },
  hidden: true
}, {
  path: "/auth-redirect",
  component: function component() {
    return import("@/views/login/auth-redirect");
  },
  hidden: true
}, {
  path: "/404",
  component: function component() {
    return import("@/views/error-page/404");
  },
  hidden: true
}, {
  path: "/401",
  component: function component() {
    return import("@/views/error-page/401");
  },
  hidden: true
}, {
  path: "/",
  component: Layout,
  redirect: "/dashboard",
  children: [{
    path: "dashboard",
    component: function component() {
      return import("@/views/dashboard/index");
    },
    name: "Dashboard",
    meta: {
      title: "Dashboard",
      icon: "dashboard",
      affix: false
    }
  }]
}, {
  path: "/profile",
  component: Layout,
  redirect: "/profile/index",
  hidden: true,
  children: [{
    path: "index",
    component: function component() {
      return import("@/views/profile/index");
    },
    name: "Profile",
    meta: {
      title: "Profile",
      icon: "user",
      noCache: true
    }
  }],
  meta: {
    roles: ["editor"]
  }
}];
/**
 * asyncRoutes
 * the routes that need to be dynamically loaded based on user roles
 */

export var asyncRoutes = [{
  path: "/icon",
  component: Layout,
  children: [{
    path: "index",
    component: function component() {
      return import("@/views/icons/index");
    },
    name: "Icons",
    meta: {
      title: "Icons",
      icon: "icon",
      noCache: true
    }
  }]
},
/** when your routing map is too long, you can split it into small modules **/
tableRouter, {
  path: "/error",
  component: Layout,
  redirect: "noRedirect",
  name: "ErrorPages",
  meta: {
    title: "Error Pages",
    icon: "404"
  },
  children: [{
    path: "401",
    component: function component() {
      return import("@/views/error-page/401");
    },
    name: "Page401",
    meta: {
      title: "401",
      noCache: true
    }
  }, {
    path: "404",
    component: function component() {
      return import("@/views/error-page/404");
    },
    name: "Page404",
    meta: {
      title: "404",
      noCache: true
    }
  }]
}, {
  path: "/theme",
  component: Layout,
  children: [{
    path: "index",
    component: function component() {
      return import("@/views/theme/index");
    },
    name: "Theme",
    meta: {
      title: "Theme",
      icon: "theme"
    }
  }]
}, // 404 page must be placed at the end !!!
{
  path: "*",
  redirect: "/404",
  hidden: true
}];

var createRouter = function createRouter() {
  return new Router({
    mode: "history",
    // require service support
    scrollBehavior: function scrollBehavior() {
      return {
        y: 0
      };
    },
    routes: constantRoutes
  });
};

var router = createRouter(); // Detail see: https://github.com/vuejs/vue-router/issues/1234#issuecomment-357941465

export function resetRouter() {
  var newRouter = createRouter();
  router.matcher = newRouter.matcher; // reset router
}
export default router;
